import React, { Component } from "react";
import axios from "axios";

class Movie extends Component {
  constructor() {
    super();

    this.state = {
      keyword: ""
    };
  }

  // componentWillMount() {
  //   axios.get("http://localhost:3000/").then(res => {
  //     this.setState({
  //       items: res.data
  //     });
  //   });
  // }
  keywordChanged = e => this.setState({ keyword: e.target.value });

  searchMovie = () =>
    fetch("http://www.omdbapi.com/?apikey=539d07a9&s=batman").then(res =>
      res.json().then(this.renderMovies)
    );

  renderMovies = res => {
    this.state = {
      movies: res.Search
    };
  };

  render() {
    return (
      <React.Fragment>
        <div>
          <input
            value={this.state.keyword}
            onChange={this.keywordChanged}
            placeholder="keyword"
          />

          <button onClick={this.searchMovie}>Search</button>
        </div>
        <div>{this.state.movies.map(movie => movie.Title)}</div>
      </React.Fragment>
    );
  }
}

export default Movie;
